function Match_substr(a,b,c)local d,e=string.find(a,b,c)if not d then return nil end;return string.sub(a,d,e),e end;function Match_remove_substr(a,b,f,c)local g,e=Match_substr(a,b,c):gsub(f,"")return g,e end;function Split_string(h,i)for j in h:gmatch("[^"..(i or"%s").."]+")do coroutine.yield(j)end end;function Dump(k,l)if l==nil then l=0 end;if type(k)=='table'then if k.print==nil then local m='{ 'for n,o in pairs(k)do if type(n)~='number'then n='"'..n..'"'end;m=m..string.rep("\t",l)..'\n['..n..'] = '..Dump(o,l+1)..','end;return m..'} 'else return k:print():gsub("\n","\n"..string.rep("\t",l-1))end else return tostring(k)end end